namespace MittagQI\Translate5\Test\Integration\Services\Connector\TagHandler;

use editor_Services_Connector_TagHandler_T5MemoryXliff;
use PHPUnit\Framework\TestCase;
use ZfExtended_Factory;

class T5MemoryXliffTest extends TestCase
{
    public function testPrepareQuery(): void
    {
        $tagHandler = ZfExtended_Factory::get(
            editor_Services_Connector_TagHandler_T5MemoryXliff::class,
            [[
                'gTagPairing' => false,
            ]]
        );
        $tagHandler->setLanguages(5, 6);

        self::assertSame(
            '<t5:n id="1" r="09eIKa6Jq4nR0NSI1tWOtdeINtS1jI1JqTbQMarV0aw2rNUAcoyBTC0wHaMXk6KtqaERowfSqKKpWaOhA2OBqJoYTU39UgA=" n="123,456.789"/> Übersetzungsbüro [ ] 24translate <t5:n id="2" r="ZGVmYXVsdCBZLW0tZA==" n="2023-09-15"/> and <t5:n id="3" r="ZGVmYXVsdCBZLW0tZA==" n="2024-10-19"/>',
            $tagHandler->prepareQuery('<div class="single  number internal-tag ownttip"><span title="&lt;1/&gt; CP: default simple" class="short">&lt;1/&gt;</span><span data-originalid="number" data-length="11" data-source="123,456.789" data-target="123,456.789" class="full"></span></div> Übersetzungsbüro [<div class="single 636861722074733d226332613022206c656e6774683d2231222f nbsp internal-tag ownttip"><span title="&lt;2/&gt;: No-Break Space (NBSP)" class="short">&lt;2/&gt;</span><span data-originalid="char" data-length="1" class="full">⎵</span></div>] 24translate <div class="single 6e756d62657220747970653d226461746522206e616d653d2264656661756c7420592d6d2d642220736f757263653d22323032332d30392d3135222069736f3d22323032332d30392d313522207461726765743d22392f31352f3233222f number internal-tag ownttip"><span title="&lt;3/&gt; CP: default simple" class="short">&lt;3/&gt;</span><span data-originalid="number" data-length="7" data-source="2023-09-15" data-target="9/15/23" class="full"></span></div> and <div class="single 6e756d62657220747970653d226461746522206e616d653d2264656661756c7420592d6d2d642220736f757263653d22323032342d31302d3139222069736f3d22323032342d31302d313922207461726765743d2231302f31392f3234222f number internal-tag ownttip"><span title="&lt;4/&gt; CP: default simple" class="short">&lt;4/&gt;</span><span data-originalid="number" data-length="8" data-source="2024-10-19" data-target="10/19/24" class="full"></span></div>')
        );
    }

    /**
     * @dataProvider restoreProvider
     */
    public function testRestoreInResult(string $source, string $expected, string $resultString): void
    {
        $tagHandler = ZfExtended_Factory::get(
            editor_Services_Connector_TagHandler_T5MemoryXliff::class,
            [[
                'gTagPairing' => false,
            ]]
        );
        $tagHandler->setLanguages(5, 6);

        // we need this step to fill \editor_Services_Connector_TagHandler_T5MemoryXliff::$numberTagMap
        $tagHandler->prepareQuery($source);

        self::assertSame(
            $expected,
            $tagHandler->restoreInResult($resultString)
        );
    }

