    /**
     * @dataProvider providePrepareQueryTarget
     */
    public function testPrepareQueryTarget(string $source, string $target, string $expectedTarget): void
    {
        $tagHandler = ZfExtended_Factory::get(
            editor_Services_Connector_TagHandler_T5MemoryXliff::class,
            [[
                'gTagPairing' => false,
            ]]
        );
        $tagHandler->setLanguages(5, 6);

        $tagHandler->prepareQuery($source);
        $tagHandler->setInputTagMap($tagHandler->getTagMap());
        $target = $tagHandler->prepareQuery($target, false);

        self::assertSame($expectedTarget, $target);
    }
