Ext.define('Erp.view.project.InvoiceListViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.invoicelistwindow',
    
    onSaveButtonClick:function(){
    	var me=this,
    		grid=me.getView().down('grid'),
    		store=grid.getStore();
    	
    	//foreach record in the grid check if it should be saved
    	store.each(function(record,id){
    		if(record.get('columnSelect')==true){
    			record.save();
    		}
    	});
    	
    	me.getView().destroy();
    },
    
    onCancelButtonClick:function(){
    	this.getView().destroy();
    }
});
