Ext.define('Erp.view.purchaseOrders.VendorPdf.VendorPdfWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.vendorpdfwindow',

    save:function(){
        this.fireEvent('savePdfClick');
        this.getView().destroy();
    },
    cancel:function(){
        this.getView().destroy();
    },
    onWindowAfterRender:function(){
        var form = this.getView().down('form');
        form.submit({target:'pdfit'});
    },
    onWindowRender:function(win,eOpts){
        var screenHeight=Ext.getBody().getViewSize().height;
        win.setHeight(screenHeight * 0.9);
    }
});
